import { setGlobal } from "reactn";
import client from "../client";
import ProjectRowItem from "../components/ProjectRowItem";

function ProjectRow(props) {
	const { content, id, total, clickEnabled } = props;

	const enableViewer = (viewerID, slideIndex) => {
		// return async function() {
		// 	const res = await client.fetch(`*[_id == "${viewerID}"]`);
		// 	const content = res[0].content;
		// 	console.log(content);
		// 	setGlobal({
		// 		viewerContent: content,
		// 		viewerID,
		// 		slideIndex
		// 	});
		// };
		client.fetch(`*[_id == "${viewerID}"]`).then(query => {
			console.log(query);

			const { intro, references, references_left, references_right } = query;
		});
	};

	let rowIndex = 0;
	return (
		<ul>
			{content.map(({ _type, media, media_left, media_right }, i) => {
				rowIndex++;
				return (
					<li
						key={rowIndex}
						onMouseUp={() => {
							clickEnabled && enableViewer(id, i);
						}}
					>
						{(media_left || media) && (
							<ProjectRowItem
								media={media ? media : media_left}
								rowIndex={rowIndex}
								total={total}
							/>
						)}
						{media_right && (
							<ProjectRowItem
								media={media_right}
								rowIndex={rowIndex + 1}
								total={total}
							/>
						)}
					</li>
				);
				media_right && rowIndex++;
			})}
		</ul>
	);
}

export default ProjectRow;
